    projectId: exampleGit.projectId,
    repositoryId: exampleGit.id,
    principal: example_group.then(example_group => example_group.id),
    permissions: {
        RemoveOthersLocks: "Allow",
        ManagePermissions: "Deny",
        CreateTag: "Deny",
        CreateBranch: "NotSet",
    },
});
